import{_ as r,c as t,o as e,ag as o}from"./chunks/framework.CHyik68U.js";const q=JSON.parse('{"title":"🐺 狼人杀对局记录 - 2025年12月3日","description":"","frontmatter":{},"headers":[],"relativePath":"games/20251203.md","filePath":"games/20251203.md","lastUpdated":1764734789000}'),l={name:"games/20251203.md"};function n(s,a,i,h,d,u){return e(),t("div",null,[...a[0]||(a[0]=[o('<h1 id="🐺-狼人杀对局记录-2025年12月3日" tabindex="-1">🐺 狼人杀对局记录 - 2025年12月3日 <a class="header-anchor" href="#🐺-狼人杀对局记录-2025年12月3日" aria-label="Permalink to &quot;🐺 狼人杀对局记录 - 2025年12月3日&quot;">​</a></h1><p><strong>游戏时间：</strong> 2025年12月3日<br><strong>游戏模式：</strong> 9人局 (3狼人 + 3村民 + 1预言家 + 1女巫 + 1猎人)<br><strong>游戏结果：</strong> 🏆 村民阵营获胜！<br><strong>游戏时长：</strong> 4天3夜</p><h2 id="🎭-角色分配" tabindex="-1">🎭 角色分配 <a class="header-anchor" href="#🎭-角色分配" aria-label="Permalink to &quot;🎭 角色分配&quot;">​</a></h2><table tabindex="0"><thead><tr><th>角色</th><th>玩家</th><th>结果</th></tr></thead><tbody><tr><td>🐺 <strong>狼人</strong></td><td>Player5, Player1, Player3</td><td>全部淘汰</td></tr><tr><td>👨‍🌾 <strong>村民</strong></td><td>Player7, Player9, Player8</td><td>获胜</td></tr><tr><td>🔮 <strong>预言家</strong></td><td>Player4</td><td>淘汰</td></tr><tr><td>🧙‍♀️ <strong>女巫</strong></td><td>Player2</td><td>淘汰</td></tr><tr><td>🏹 <strong>猎人</strong></td><td>Player6</td><td>获胜</td></tr></tbody></table><h2 id="📊-游戏统计" tabindex="-1">📊 游戏统计 <a class="header-anchor" href="#📊-游戏统计" aria-label="Permalink to &quot;📊 游戏统计&quot;">​</a></h2><ul><li><strong>平安夜：</strong> 1次（第一夜）</li><li><strong>女巫用药：</strong> 2次（解药1次，毒药1次）</li><li><strong>预言家查验：</strong> 2次（Player1-狼人，Player5-狼人）</li><li><strong>投票轮次：</strong> 3轮</li><li><strong>最高票数：</strong> 5票（Player5被淘汰）</li></ul><hr><h2 id="🌙-第一夜-平安夜的神迹" tabindex="-1">🌙 第一夜：平安夜的神迹 <a class="header-anchor" href="#🌙-第一夜-平安夜的神迹" aria-label="Permalink to &quot;🌙 第一夜：平安夜的神迹&quot;">​</a></h2><h3 id="🤝-狼人密谋" tabindex="-1">🤝 狼人密谋 <a class="header-anchor" href="#🤝-狼人密谋" aria-label="Permalink to &quot;🤝 狼人密谋&quot;">​</a></h3><p><strong>Player1:</strong> &quot;建议刀Player7，首夜随机刀容易藏身份，选中置位玩家减少怀疑。&quot;<br><strong>Player3:</strong> &quot;同意刀Player7，首夜随机选择中置位玩家，降低暴露风险。&quot;<br><strong>Player5:</strong> &quot;同意刀Player7，首夜随机刀减少暴露风险，白天统一伪装村民，避免暴露。&quot;</p><p><strong>🎯 狼人目标：</strong> Player7（村民）</p><h3 id="🧙‍♀️-女巫救援" tabindex="-1">🧙‍♀️ 女巫救援 <a class="header-anchor" href="#🧙‍♀️-女巫救援" aria-label="Permalink to &quot;🧙‍♀️ 女巫救援&quot;">​</a></h3><p><strong>关键时刻：</strong> 女巫Player2使用解药救下Player7！</p><blockquote><p>&quot;我选择救Player7，因为保持潜在的预言家或关键村民存活对早期游戏至关重要，第一夜失去他们会严重不利于村民阵营。&quot;</p></blockquote><h3 id="🔮-预言家洞察" tabindex="-1">🔮 预言家洞察 <a class="header-anchor" href="#🔮-预言家洞察" aria-label="Permalink to &quot;🔮 预言家洞察&quot;">​</a></h3><p><strong>预言家Player4查验：</strong> Player1 → <strong>狼人</strong> ✅</p><blockquote><p>&quot;首夜无信息，优先查前置位或行为模糊者，Player1发言少、存在感低，可能是狼人藏身份，需优先验证。&quot;</p></blockquote><hr><h2 id="☀️-第一天-智慧与怀疑的较量" tabindex="-1">☀️ 第一天：智慧与怀疑的较量 <a class="header-anchor" href="#☀️-第一天-智慧与怀疑的较量" aria-label="Permalink to &quot;☀️ 第一天：智慧与怀疑的较量&quot;">​</a></h2><h3 id="📢-系统宣布" tabindex="-1">📢 系统宣布 <a class="header-anchor" href="#📢-系统宣布" aria-label="Permalink to &quot;📢 系统宣布&quot;">​</a></h3><blockquote><p>🌅 <strong>天亮了，请所有玩家睁眼。昨晚平安夜，无人被淘汰。</strong></p></blockquote><h3 id="💬-玩家发言精选" tabindex="-1">💬 玩家发言精选 <a class="header-anchor" href="#💬-玩家发言精选" aria-label="Permalink to &quot;💬 玩家发言精选&quot;">​</a></h3><p><strong>Player7</strong>（被救的村民）:&quot;我是村民，平安夜大概率是女巫救人，狼人可能在试探。现在信息极少，建议别信轻易跳身份的人，尤其注意谁在带节奏或强行分析。&quot;</p><p><strong>Player4</strong>（预言家）:&quot;我是村民，首夜无信息。平安夜说明女巫用药救了人，这是重要信息。建议大家别乱跳身份，先观察发言找漏洞。&quot;</p><p><strong>Player5</strong>（狼人伪装）:&quot;平安夜大概率女巫救了人，狼队可能在试探。我是村民，建议别乱跳身份，重点观察谁对平安夜反应异常或急于带节奏。&quot;</p><h3 id="🗳️-投票结果" tabindex="-1">🗳️ 投票结果 <a class="header-anchor" href="#🗳️-投票结果" aria-label="Permalink to &quot;🗳️ 投票结果&quot;">​</a></h3><p><strong>Player3</strong> 获得3票被淘汰！</p><blockquote><p>&quot;我是村民，被投票出局很遗憾。平安夜局势下，末置位沉默容易成为排查目标，下次应更早发言表明立场。&quot;</p></blockquote><hr><h2 id="🌙-第二夜-预言家的牺牲" tabindex="-1">🌙 第二夜：预言家的牺牲 <a class="header-anchor" href="#🌙-第二夜-预言家的牺牲" aria-label="Permalink to &quot;🌙 第二夜：预言家的牺牲&quot;">​</a></h2><h3 id="🤝-狼人密谋-1" tabindex="-1">🤝 狼人密谋 <a class="header-anchor" href="#🤝-狼人密谋-1" aria-label="Permalink to &quot;🤝 狼人密谋&quot;">​</a></h3><p><strong>Player1:</strong> &quot;Player4跳预言家查杀Player1，威胁极大，必须今晚刀掉Player4，否则白天必被投出。&quot;<br><strong>Player5:</strong> &quot;同意刀Player4，清除最大威胁。&quot;</p><p><strong>🎯 狼人目标：</strong> Player4（预言家）</p><h3 id="🧙‍♀️-女巫复仇" tabindex="-1">🧙‍♀️ 女巫复仇 <a class="header-anchor" href="#🧙‍♀️-女巫复仇" aria-label="Permalink to &quot;🧙‍♀️ 女巫复仇&quot;">​</a></h3><p><strong>女巫Player2使用毒药：</strong> 毒杀Player1（狼人）！</p><blockquote><p>&quot;我在Player1身上使用毒药，因为Player4声称是预言家并验证了Player1是狼人——如果这是真的，现在消灭Player1可以移除一个确认的威胁；如果是假的，可以测试Player4的诚实性。无论哪种方式，都能给隐藏的狼人施加压力。&quot;</p></blockquote><h3 id="🔮-预言家最后查验" tabindex="-1">🔮 预言家最后查验 <a class="header-anchor" href="#🔮-预言家最后查验" aria-label="Permalink to &quot;🔮 预言家最后查验&quot;">​</a></h3><p><strong>预言家Player4查验：</strong> Player5 → <strong>狼人</strong> ✅</p><blockquote><p>&quot;查验Player5。Player3刚被投出，若其为狼，则Player5可能是其队友；若Player3是村民，则Player5投票行为可疑（投我），且狼队可能由Player1、Player5、Player9组成。需尽快确认Player5身份以锁定狼坑。&quot;</p></blockquote><hr><h2 id="☀️-第二天-真相大白" tabindex="-1">☀️ 第二天：真相大白 <a class="header-anchor" href="#☀️-第二天-真相大白" aria-label="Permalink to &quot;☀️ 第二天：真相大白&quot;">​</a></h2><h3 id="📢-系统宣布-1" tabindex="-1">📢 系统宣布 <a class="header-anchor" href="#📢-系统宣布-1" aria-label="Permalink to &quot;📢 系统宣布&quot;">​</a></h3><blockquote><p>🌅 <strong>天亮了，请所有玩家睁眼。昨晚被淘汰的玩家有：Player4, and Player1。</strong></p></blockquote><h3 id="💬-关键发言" tabindex="-1">💬 关键发言 <a class="header-anchor" href="#💬-关键发言" aria-label="Permalink to &quot;💬 关键发言&quot;">​</a></h3><p><strong>Player4遗言</strong>（预言家）:</p><blockquote><p>&quot;我是预言家，已查验Player1和Player5均为狼人，Player3极大概率是第三狼。当前必须集中投票淘汰Player5，以阻止狼队夜间继续刀人。请所有村民相信验人信息，跟我投Player5，尽快清狼结束游戏。&quot;</p></blockquote><p><strong>Player5</strong>（狼人挣扎）:</p><blockquote><p>&quot;Player4跳预言家查杀Player1，但平安夜后狼队可能已调整策略。作为村民，我质疑Player4身份真实性——若为真预言家，应更早跳明；若为假跳，则极可能是狼人倒打一耙。我投Player4，优先排除高风险假神职。&quot;</p></blockquote><hr><h2 id="🌙-第三夜-最后的较量" tabindex="-1">🌙 第三夜：最后的较量 <a class="header-anchor" href="#🌙-第三夜-最后的较量" aria-label="Permalink to &quot;🌙 第三夜：最后的较量&quot;">​</a></h2><h3 id="🎯-狼人目标-player2-女巫" tabindex="-1">🎯 狼人目标：Player2（女巫） <a class="header-anchor" href="#🎯-狼人目标-player2-女巫" aria-label="Permalink to &quot;🎯 狼人目标：Player2（女巫）&quot;">​</a></h3><p>狼人Player5孤注一掷，选择淘汰女巫Player2。</p><h3 id="📢-系统宣布-2" tabindex="-1">📢 系统宣布 <a class="header-anchor" href="#📢-系统宣布-2" aria-label="Permalink to &quot;📢 系统宣布&quot;">​</a></h3><blockquote><p>🌅 <strong>天亮了，请所有玩家睁眼。昨晚被淘汰的玩家有：Player2, and Player9。</strong></p></blockquote><hr><h2 id="🏁-最终决战-正义的胜利" tabindex="-1">🏁 最终决战：正义的胜利 <a class="header-anchor" href="#🏁-最终决战-正义的胜利" aria-label="Permalink to &quot;🏁 最终决战：正义的胜利&quot;">​</a></h2><h3 id="🗳️-最终投票" tabindex="-1">🗳️ 最终投票 <a class="header-anchor" href="#🗳️-最终投票" aria-label="Permalink to &quot;🗳️ 最终投票&quot;">​</a></h3><p><strong>Player5</strong> 获得5票，被淘汰！</p><h3 id="🐺-狼人遗言" tabindex="-1">🐺 狼人遗言 <a class="header-anchor" href="#🐺-狼人遗言" aria-label="Permalink to &quot;🐺 狼人遗言&quot;">​</a></h3><p><strong>Player5:</strong></p><blockquote><p>&quot;作为狼人，我前期配合队友刀神、抗推预言家的策略基本正确，但Player4跳明后未能有效洗清Player1身份，导致被女巫毒杀和投票出局。&quot;</p></blockquote><h3 id="🏆-系统宣布胜利" tabindex="-1">🏆 系统宣布胜利 <a class="header-anchor" href="#🏆-系统宣布胜利" aria-label="Permalink to &quot;🏆 系统宣布胜利&quot;">​</a></h3><blockquote><p><strong>所有狼人已被淘汰！游戏结束，村民阵营获胜！</strong> 🏘️🎉</p><p>本局游戏中，所有玩家的真实身份为：Player5、Player1、Player3是狼人，Player7、Player9、Player8是村民，Player4是预言家，Player6是猎人，Player2是女巫。</p></blockquote><hr><h2 id="🎯-精彩亮点" tabindex="-1">🎯 精彩亮点 <a class="header-anchor" href="#🎯-精彩亮点" aria-label="Permalink to &quot;🎯 精彩亮点&quot;">​</a></h2><h3 id="✨-关键决策" tabindex="-1">✨ 关键决策 <a class="header-anchor" href="#✨-关键决策" aria-label="Permalink to &quot;✨ 关键决策&quot;">​</a></h3><ol><li><strong>女巫救援：</strong> 第一夜使用解药创造平安夜，为村民阵营争取宝贵时间</li><li><strong>预言家洞察：</strong> 准确查验两名狼人，为投票提供关键依据</li><li><strong>女巫复仇：</strong> 精准毒杀狼人Player1，展现神职联动威力</li><li><strong>村民团结：</strong> 最终5票统一淘汰狼人Player5，体现团队智慧</li></ol><h3 id="🏆-mvp表现" tabindex="-1">🏆 MVP表现 <a class="header-anchor" href="#🏆-mvp表现" aria-label="Permalink to &quot;🏆 MVP表现&quot;">​</a></h3><ul><li><strong>最佳神职：</strong> 女巫Player2 - 解药毒药双命中，完美发挥</li><li><strong>最佳村民：</strong> Player7 - 平安夜后分析到位，生存到最后</li><li><strong>最佳猎人：</strong> Player6 - 关键时刻表明身份，施加威慑</li></ul><hr><h2 id="📈-战术分析" tabindex="-1">📈 战术分析 <a class="header-anchor" href="#📈-战术分析" aria-label="Permalink to &quot;📈 战术分析&quot;">​</a></h2><h3 id="🔍-狼人策略得失" tabindex="-1">🔍 狼人策略得失 <a class="header-anchor" href="#🔍-狼人策略得失" aria-label="Permalink to &quot;🔍 狼人策略得失&quot;">​</a></h3><p><strong>成功之处：</strong></p><ul><li>首夜随机刀选中置位玩家，降低暴露风险</li><li>白天统一伪装村民，避免暴露狼队身份</li><li>及时清除预言家威胁，展现战术意识</li></ul><p><strong>失败之处：</strong></p><ul><li>未能有效应对平安夜局势，被动挨打</li><li>预言家跳明后缺乏有效反制策略</li><li>最终孤注一掷未能扭转败局</li></ul><h3 id="💪-村民胜利要素" tabindex="-1">💪 村民胜利要素 <a class="header-anchor" href="#💪-村民胜利要素" aria-label="Permalink to &quot;💪 村民胜利要素&quot;">​</a></h3><ul><li><strong>神职联动：</strong> 预言家、女巫、猎人形成有效配合</li><li><strong>信息利用：</strong> 充分利用平安夜和验人信息</li><li><strong>投票统一：</strong> 关键时刻5票集中淘汰狼人</li><li><strong>身份保护：</strong> 神职合理隐藏，避免过早暴露</li></ul><hr><p><em>本记录基于 Eino ADK 框架生成的智能狼人杀游戏对话</em><br><em>记录时间：2025年12月3日</em></p>',80)])])}const g=r(l,[["render",n]]);export{q as __pageData,g as default};
