import{_ as a,c as l,o as t,ag as o}from"./chunks/framework.CHyik68U.js";const b=JSON.parse('{"title":"🎭 狼人杀角色介绍","description":"","frontmatter":{},"headers":[],"relativePath":"roles/index.md","filePath":"roles/index.md","lastUpdated":1764735498000}'),i={name:"roles/index.md"};function n(e,r,s,h,g,d){return t(),l("div",null,[...r[0]||(r[0]=[o('<h1 id="🎭-狼人杀角色介绍" tabindex="-1">🎭 狼人杀角色介绍 <a class="header-anchor" href="#🎭-狼人杀角色介绍" aria-label="Permalink to &quot;🎭 狼人杀角色介绍&quot;">​</a></h1><p>狼人杀是一款经典的社交推理游戏，每个角色都有独特的技能和使命。在基于 Eino ADK 的智能狼人杀系统中，每个角色都由大语言模型扮演，展现出丰富的个性和策略思维。</p><h2 id="🐺-狼人阵营" tabindex="-1">🐺 狼人阵营 <a class="header-anchor" href="#🐺-狼人阵营" aria-label="Permalink to &quot;🐺 狼人阵营&quot;">​</a></h2><h3 id="🐺-狼人" tabindex="-1">🐺 狼人 <a class="header-anchor" href="#🐺-狼人" aria-label="Permalink to &quot;🐺 狼人&quot;">​</a></h3><p><strong>人数：</strong> 3人<br><strong>目标：</strong> 淘汰所有村民和神职<br><strong>技能：</strong> 每晚可以集体选择淘汰一名玩家<br><strong>策略：</strong> 隐藏身份，混淆视听，逐个击破</p><p><strong>AI角色特点：</strong></p><ul><li>善于伪装成村民，发表看似合理的分析</li><li>夜间进行战术讨论，选择最优淘汰目标</li><li>白天统一口径，避免暴露狼队身份</li><li>关键时刻敢于带节奏，引导投票方向</li></ul><hr><h2 id="🏘️-村民阵营" tabindex="-1">🏘️ 村民阵营 <a class="header-anchor" href="#🏘️-村民阵营" aria-label="Permalink to &quot;🏘️ 村民阵营&quot;">​</a></h2><h3 id="👨‍🌾-村民" tabindex="-1">👨‍🌾 村民 <a class="header-anchor" href="#👨‍🌾-村民" aria-label="Permalink to &quot;👨‍🌾 村民&quot;">​</a></h3><p><strong>人数：</strong> 3人<br><strong>目标：</strong> 找出并淘汰所有狼人<br><strong>技能：</strong> 白天投票权<br><strong>策略：</strong> 观察发言，分析逻辑，团结一致</p><p><strong>AI角色特点：</strong></p><ul><li>基于有限信息进行合理推理</li><li>关注发言漏洞和行为异常</li><li>谨慎使用投票权，避免误投好人</li><li>关键时刻敢于表明立场</li></ul><h3 id="🔮-预言家" tabindex="-1">🔮 预言家 <a class="header-anchor" href="#🔮-预言家" aria-label="Permalink to &quot;🔮 预言家&quot;">​</a></h3><p><strong>人数：</strong> 1人<br><strong>目标：</strong> 利用查验信息帮助村民获胜<br><strong>技能：</strong> 每晚可查验一名玩家身份<br><strong>策略：</strong> 合理隐藏身份，关键时刻跳明身份</p><p><strong>AI角色特点：</strong></p><ul><li>谨慎选择查验目标，优先查验可疑玩家</li><li>平衡信息公开与身份保护的关系</li><li>跳明身份时提供详细的查验逻辑</li><li>遗言中传达关键验人信息</li></ul><h3 id="🧙‍♀️-女巫" tabindex="-1">🧙‍♀️ 女巫 <a class="header-anchor" href="#🧙‍♀️-女巫" aria-label="Permalink to &quot;🧙‍♀️ 女巫&quot;">​</a></h3><p><strong>人数：</strong> 1人<br><strong>目标：</strong> 合理使用药水帮助村民获胜<br><strong>技能：</strong> 拥有一瓶解药和一瓶毒药<br><strong>策略：</strong> 关键时刻用药，扭转局势</p><p><strong>AI角色特点：</strong></p><ul><li>解药使用谨慎，优先救助关键角色</li><li>毒药使用果断，精准打击确认狼人</li><li>根据游戏进程调整用药策略</li><li>平安夜创造重要信息和优势</li></ul><h3 id="🏹-猎人" tabindex="-1">🏹 猎人 <a class="header-anchor" href="#🏹-猎人" aria-label="Permalink to &quot;🏹 猎人&quot;">​</a></h3><p><strong>人数：</strong> 1人<br><strong>目标：</strong> 在被淘汰时带走狼人<br><strong>技能：</strong> 被淘汰时可选择带走一名玩家<br><strong>策略：</strong> 隐藏身份，关键时刻施加威慑</p><p><strong>AI角色特点：</strong></p><ul><li>前期隐藏猎人身份，避免过早暴露</li><li>关键时刻表明身份，施加威慑压力</li><li>被淘汰时精准选择目标，最大化价值</li><li>与神职形成联动，保护村民阵营</li></ul><hr><h2 id="🎯-游戏流程" tabindex="-1">🎯 游戏流程 <a class="header-anchor" href="#🎯-游戏流程" aria-label="Permalink to &quot;🎯 游戏流程&quot;">​</a></h2><h3 id="🌙-夜晚阶段" tabindex="-1">🌙 夜晚阶段 <a class="header-anchor" href="#🌙-夜晚阶段" aria-label="Permalink to &quot;🌙 夜晚阶段&quot;">​</a></h3><ol><li><strong>狼人讨论：</strong> 狼人集体选择淘汰目标</li><li><strong>女巫决策：</strong> 决定是否使用解药或毒药</li><li><strong>预言家查验：</strong> 选择一名玩家进行身份查验</li><li><strong>系统宣布：</strong> 公布夜晚结果</li></ol><h3 id="☀️-白天阶段" tabindex="-1">☀️ 白天阶段 <a class="header-anchor" href="#☀️-白天阶段" aria-label="Permalink to &quot;☀️ 白天阶段&quot;">​</a></h3><ol><li><strong>讨论发言：</strong> 所有存活玩家依次发言</li><li><strong>投票淘汰：</strong> 集体投票淘汰一名玩家</li><li><strong>遗言发表：</strong> 被淘汰玩家发表最后遗言</li><li><strong>结果宣布：</strong> 公布投票结果和游戏状态</li></ol><hr><h2 id="🤖-ai智能特点" tabindex="-1">🤖 AI智能特点 <a class="header-anchor" href="#🤖-ai智能特点" aria-label="Permalink to &quot;🤖 AI智能特点&quot;">​</a></h2><h3 id="智能对话" tabindex="-1">智能对话 <a class="header-anchor" href="#智能对话" aria-label="Permalink to &quot;智能对话&quot;">​</a></h3><ul><li><strong>自然语言：</strong> 使用流畅的中文进行游戏对话</li><li><strong>逻辑推理：</strong> 基于游戏规则和已知信息进行合理推理</li><li><strong>情感表达：</strong> 展现角色的个性和情感状态</li><li><strong>策略思考：</strong> 根据游戏进程调整策略和行为</li></ul><h3 id="角色扮演" tabindex="-1">角色扮演 <a class="header-anchor" href="#角色扮演" aria-label="Permalink to &quot;角色扮演&quot;">​</a></h3><ul><li><strong>身份保密：</strong> 狼人隐藏身份，神职合理暴露</li><li><strong>行为一致：</strong> 言行符合角色设定和游戏逻辑</li><li><strong>团队协作：</strong> 狼人团队配合，神职联动保护</li><li><strong>适应变化：</strong> 根据游戏局势调整策略和目标</li></ul><h3 id="学习进化" tabindex="-1">学习进化 <a class="header-anchor" href="#学习进化" aria-label="Permalink to &quot;学习进化&quot;">​</a></h3><ul><li><strong>经验积累：</strong> 从每局游戏中学习和改进</li><li><strong>策略优化：</strong> 不断优化角色策略和决策逻辑</li><li><strong>平衡调整：</strong> 保持游戏的平衡性和趣味性</li><li><strong>个性发展：</strong> 每个AI角色都有独特的思考模式</li></ul><hr><h2 id="📊-胜负判定" tabindex="-1">📊 胜负判定 <a class="header-anchor" href="#📊-胜负判定" aria-label="Permalink to &quot;📊 胜负判定&quot;">​</a></h2><h3 id="村民阵营胜利条件" tabindex="-1">村民阵营胜利条件 <a class="header-anchor" href="#村民阵营胜利条件" aria-label="Permalink to &quot;村民阵营胜利条件&quot;">​</a></h3><ul><li>所有狼人被投票淘汰</li><li>狼人无法在夜间达成淘汰目标</li></ul><h3 id="狼人阵营胜利条件" tabindex="-1">狼人阵营胜利条件 <a class="header-anchor" href="#狼人阵营胜利条件" aria-label="Permalink to &quot;狼人阵营胜利条件&quot;">​</a></h3><ul><li>狼人数量 ≥ 村民数量</li><li>所有神职被淘汰且狼人存活</li></ul><hr><p><em>基于 Eino ADK 框架构建的智能狼人杀系统</em><br><em>每个角色都由大语言模型智能扮演，带来真实而精彩的游戏体验</em></p>',47)])])}const c=a(i,[["render",n]]);export{b as __pageData,c as default};
